int main(void)
{
  HAL_Init();
  SystemClock_Config();
  Keypad_Init();
  GPIO_init();
  spi_setup();
  tim2_setup();
  __enable_irq();
  int8_t button;
  while (1)
  {
	  //wait for button press
	  while(Keypad_GetButton() == -1);
	  button = Keypad_GetButton();
	  //wait for button release
	  while(Keypad_GetButton() != -1);
	  switch(button) {
	    case (1):
	    		//set frequency to 100;
	    		freq_out = 1;
	    		break;
	    case (2):
	    	    //set frequency to 200;
	    		freq_out = 2;
	    		break;
	    case (3):
	    	    //set frequency to 300;
	    		freq_out = 3;
			    break;
	    case (4):
	    	    //set frequency to 400;
	    		freq_out = 4;
	    		break;
	    case (5):
	    	    //set frequency to 500;
	    		freq_out = 5;
	    		break;
	    		//change states
	    case (SQR_WAVE):
	    		out_wave = SQR;
	    		break;
	    case (SIN_WAVE):
	    		out_wave = SIN;
	    		break;
	    case (TRI_WAVE):
	    		out_wave = TRI;
	    		break;
	    case(SAW_WAVE):
	    		out_wave = SAW;
	    		break;
	    case(RESET):
				out_wave = SQR;   //reset to square
	    		freq_out = 1;     //reset to 100 Hz
	    		duty_out = 50;    //reset to 50% duty cycle
	    		break;
	    		//change duty cycle
	    case(DUTY_DOWN):
				if(duty_out != DUTY_MIN) {
					duty_out -= 10;
				}
			    break;
	    case(DUTY_UP):
				if(duty_out != DUTY_MAX) {
					duty_out += 10;
				}
	    		break;
	    default:
	    	break;
	  }

  }
}

void TIM2_IRQHandler(void) {
  	if(TIM2 -> SR & TIM_SR_UIF){ //check for ARR
  		switch(out_wave) {
			case SQR:
				//duty cycle% * Table Size creates high/low division
				if(curr_index > (TABLE_SIZE * duty_out)/100) {
					send_dac(VOLT_HIGH);
				}
				else {
					send_dac(VOLT_LOW);
				}
				break;
			//Based on each state output specific wave LUT
			case SIN:
				send_dac(SIN_LUT[curr_index]);
				break;
			case TRI:
				send_dac(TRI_LUT[curr_index]);
				break;
			case SAW:
				send_dac(SAW_LUT[curr_index]);
				break;
			default:
				break;
		}
  		//Effective freqeuncy multiplier
  		curr_index += freq_out;
  		if(curr_index > TABLE_SIZE) {
  			curr_index = 0;
  		}
  		TIM2 -> SR &= ~TIM_SR_UIF; //clear interrupt flag
  	}
}
void GPIO_init(void) {
	    // PA4: CS
		// PA5: SCLK
		// PA7: MOSI
		//initialize clock for pins
		RCC -> AHB2ENR |= (RCC_AHB2ENR_GPIOAEN);
		//Setup pins to be used
		GPIOA -> MODER &= ~(GPIO_MODER_MODE4 | GPIO_MODER_MODE5
				| GPIO_MODER_MODE7 | GPIO_MODER_MODE6);
		GPIOA-> MODER |= (2 << GPIO_MODER_MODE4_Pos | 2 << GPIO_MODER_MODE5_Pos
				| 2 << GPIO_MODER_MODE7_Pos | 1 << GPIO_MODER_MODE6_Pos);
		GPIOA -> OTYPER &= ~(GPIO_OTYPER_OT4 | GPIO_OTYPER_OT5
				| GPIO_OTYPER_OT7 | GPIO_OTYPER_OT6);
		GPIOA -> OSPEEDR |= (GPIO_OSPEEDR_OSPEED4 | GPIO_OSPEEDR_OSPEED5
				| GPIO_OSPEEDR_OSPEED7 | GPIO_OSPEEDR_OSPEED6);
		GPIOA -> PUPDR &= ~(GPIO_PUPDR_PUPDR4 | GPIO_PUPDR_PUPDR5
				| GPIO_PUPDR_PUPDR7 | GPIO_PUPDR_PUPDR6);
		//Set pins to alternate function, clear to set them after
		GPIOA -> AFR[0] &= ~((GPIO_AFRL_AFSEL4 << GPIO_AFRL_AFSEL4_Pos)
				| (GPIO_AFRL_AFSEL5 << GPIO_AFRL_AFSEL5_Pos) | (GPIO_AFRL_AFSEL7 << GPIO_AFRL_AFSEL7_Pos));
		//Set AFR[0] to AFR low, and use 5 because GPIO uses the 5th part
		GPIOA -> AFR[0] |= ((5 << GPIO_AFRL_AFSEL4_Pos) | (5 << GPIO_AFRL_AFSEL5_Pos)
				| (5 << GPIO_AFRL_AFSEL7_Pos));
}
void tim2_setup(void) {
	  //4 MHz
	  RCC -> APB1ENR1 |= (RCC_APB1ENR1_TIM2EN);
	  //this is a variable
	  TIM2 -> ARR = FREQ_CNT - 1;
	  //enable update interrupt
	  TIM2 -> DIER |= (TIM_DIER_UIE);
	  //clear register for update counter
	  TIM2 -> SR &= ~(TIM_SR_UIF);
	  TIM2->CR1 &= ~(TIM_CR1_DIR);
	  //enable counter & interrupt
	  TIM2 -> CR1 |= TIM_CR1_CEN;
	  // enable interrupt in NVIC
	  NVIC -> ISER[0] = (1 << TIM2_IRQn);
}

